mapper.insert:

	SELECT IFNULL(MAX(board_idx), 0) FROM notice

INSERT
INTO notice
VALUES (#{board_idx} + 1
, #{board_subject}
, #{board_content}
, #{board_file}
, #{board_file_path}
, now()
)

