mapper.select:
SELECT p.project_idx ,p.project_type ,p.project_title ,p.project_start_date ,p.project_end_date ,p.project_target ,p.project_approve_status ,(pd.project_detail_amount/p.project_target)*100 AS target_rate ,m.member_name ,pd.project_detail_amount ,pd.project_detail_person ,DATEDIFF(now(), p.project_end_date) AS d_day FROM project p JOIN member m ON p.member_idx = m.member_idx JOIN project_detail pd ON p.project_idx = pd.project_idx WHERE 1=1
AND p.project_status = ${selectOption}
ORDER BY p.project_idx DESC;

mapper.update:
	UPDATE project p
	   SET  p.project_start_date = #{project_start_date}
		   ,p.project_end_date = #{project_end_date}
		   ,p.project_coming_soon_date = #{project_coming_soon_date}
		   ,p.project_approve_status = 2
    WHERE p.project_idx = #{project_idx};

UPDATE project p
   SET p.project_approve_status = 3
WHERE p.project_idx = #{project_idx};

mapper.insert:

	SELECT IFNULL(MAX(approve_no), 0) FROM approve_history

INSERT INTO approve_history(
			 approve_no
			,project_idx
			,admin_name
			,approve_date
			,approve_reason
			,approve_status
			)
		VALUES(
			 #{approve_no} + 1
			,#{project_idx}
			,#{admin_name}
			,now()
			,


					#{approve_reason}


					'승인'


			,#{project_approve_status}
		)

