mapper.select:
SELECT DISTINCT p.project_idx, project_category, project_thumbnail, project_plan, project_start_date project_detail_amount, project_target, project_end_date, project_title, maker_name FROM project p INNER JOIN project_detail pd ON p.project_idx = pd.project_idx INNER JOIN maker m ON p.maker_idx = m.maker_idx WHERE project_approve_status = '2' AND project_type = '2'
AND DATE_FORMAT(NOW(), '%Y-%m-%d') >= p.project_start_date AND p.project_end_date >= DATE_FORMAT(NOW(), '%Y-%m-%d')
AND DATE_FORMAT(NOW(), '%Y-%m-%d') >= p.project_end_date
AND project_category = #{category}
ORDER BY (pd.project_detail_amount / p.project_target * 100) DESC;
ORDER BY pd.project_detail_amount DESC;
ORDER BY p.project_end_date;
ORDER BY p.project_start_date DESC;

SELECT DISTINCT p.project_idx, project_category, project_thumbnail, project_plan, project_title, m.maker_name, project_start_date, project_coming_soon_date FROM project p INNER JOIN project_detail pd ON p.project_idx = pd.project_idx INNER JOIN maker m ON p.maker_idx = m.maker_idx WHERE project_approve_status = '2' AND project_plan != '1' AND project_coming_soon_date != "" AND project_start_date > DATE(NOW())
AND project_category = #{category}
AND project_coming_soon_date >= DATE_FORMAT(NOW(), '%Y-%m-%d') ORDER BY p.project_coming_soon_date DESC;
ORDER BY project_start_date DESC;

mapper.insert:
INSERT INTO payment
	VALUES(
		null
		, now()
		, #{member_idx}
		, #{project_idx}
		, #{reward_name}
		, #{reward_amount}
		, #{used_point_amount}
		, #{used_coupon_amount}
		, #{delivery_fee}
		, #{donation}
		, #{total_amount}
		, #{delivery_address}
		, #{delivery_phone_number}
		, ""
	)

mapper.update:
UPDATE reward 
	SET reward_sell = reward_sell + #{reward_quantity}
	WHERE reward_idx = #{reward_idx}

UPDATE coupon_used
	SET coupon_useable = "N"
	WHERE coupon_idx = #{coupon_idx}

UPDATE project_detail
	SET project_detail_amount = project_detail_amount + #{total_amount}
	WHERE project_idx = #{project_idx}

mapper.insert:
INSERT INTO point
VALUES (
	null
	, #{member_idx}
	, "결제 금액 5% 포인트 적립"
	, 0
	, #{point}
	, (SELECT member_point
		FROM `point` p
		WHERE point_idx =
		(SELECT MAX(point_idx)
		FROM `point` p
		WHERE member_idx = #{member_idx}
		GROUP BY member_idx)) + #{point}
)


SELECT IFNULL(MAX(inq_idx), 0) FROM inquiry

	INSERT
		INTO inquiry
			VALUES (
				#{inq_idx} + 1
				, #{member_idx}
				, #{maker_idx}
				, '진행중'
				, #{inq_subject}
				, #{inq_content}
				, #{inq_idx} + 1
				, 0
				, 0
				, now()
				)

mapper.select:
